INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (7, 'Persona 5 Royal', 'RPG de fantasía en una Tokio moderna.', DATE '2020-03-31', 'Atlus', 'RPG', 'PS5', 'M', 'Japonés, Inglés', '50GB de espacio, conexión a internet', TIMESTAMP '2024-06-11 19:55:57.272000', TIMESTAMP '2024-06-11 19:55:57.272000', 1, 100, 'El protagonista, un estudiante transferido, que se convierte en líder de los Phantom Thieves. Junto a sus amigos, navega por Palacios, mundos formados por los deseos distorsionados de los adultos corruptos. El juego combina exploración, combates por turnos y simulación de vida diaria en Tokio. Incluye nuevos personajes, historias adicionales y mejoras en la jugabilidad, ampliando la experiencia del original Persona 5.', 49990);
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (36, 'Elden Ring', 'EL NUEVO RPG DE ACCIÓN DE FANTASÍA. Levántate, tiznado, y déjate guiar por la gracia para esgrimir el poder del Anillo de Elden y convertirte en un Señor de Elden en las Tierras Intermedias.', DATE '2022-02-25', 'FromSoftware', 'Acción, RPG', 'PC, PS4, PS5, Xbox One, Xbox Series X/S', 'Mature', 'Multilenguaje', 'Procesador: Intel Core i5-8400 | Memoria: 12 GB RAM | Gráficos: NVIDIA GeForce GTX 1060, 3 GB', TIMESTAMP '2024-06-12 15:20:49.363000', TIMESTAMP '2024-06-12 15:20:49.363000', 1, 100, 'Álzate, Sinluz, y que la gracia te guíe para abrazar el poder del Círculo de Elden y encumbrarte como señor del Círculo en las Tierras Intermedias.
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (37, 'Hollow Knight', 'Un juego de aventuras y acción en 2D con un estilo metroidvania.', DATE '2017-02-24', 'Team Cherry', 'Acción, Aventura', 'PC, Nintendo Switch, PS4, Xbox One', 'Everyone 10+', 'Inglés', 'Procesador: Intel Core 2 Duo E5200 | Memoria: 4 GB RAM | Gráficos: GeForce 9800GTX+ (1GB)', TIMESTAMP '2024-06-12 15:20:49.458000', TIMESTAMP '2024-06-12 15:20:49.458000', 1, 200, 'Ambientado en el misterioso y atmosférico mundo subterráneo de Hallownest, los jugadores controlan a un enigmático caballero mientras exploran cavernas, combaten criaturas y descubren secretos ocultos. Con un estilo artístico dibujado a mano, jugabilidad metroidvania y una narrativa inmersiva, Hollow Knight ofrece una experiencia desafiante y gratificante. El juego destaca por su profunda atmósfera, su combate preciso y su vasto mundo interconectado.', 14990);
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (38, 'Terraria', 'Un juego de sandbox de acción y aventura con un estilo de juego libre y creativo.', DATE '2011-05-16', 'Re-Logic', 'Aventura, Sandbox', 'PC, PS5, Xbox Series X/S, Nintendo Switch', 'Teen', 'Multilenguaje', 'Procesador: Dual Core 3.0 Ghz | Memoria: 4 GB RAM | Gráficos: 256mb Video Memory, capable of Shader Model 2.0+', TIMESTAMP '2024-06-12 15:20:49.577000', TIMESTAMP '2024-06-12 15:20:49.577000', 1, 150, 'Terraria es un juego sandbox, de acción y aventura creado por Andrew "Redigit" Spinks y producido de forma independiente por el estudio Re-Logic. Tiene características tales como la exploración, la artesanía, la construcción de estructuras, combatir diversos monstruos y jefes.
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (42, 'Zelda Skyward Sword HD', 'La aventura clásica de Zelda remasterizada en alta definición.', DATE '2021-07-16', 'Nintendo', 'Aventura/Acción', 'Nintendo Switch', 'E', 'Japonés, Inglés, Español', '8GB de espacio, conexión a internet', TIMESTAMP '2024-06-12 22:39:09.070000', TIMESTAMP '2024-06-12 22:39:09.070000', 1, 200, 'Desciende al peligroso mundo de la superficie en esta aventura por los cielos. Durante tu misión explorarás peligrosas mazmorras llenas de rompecabezas. Cada objeto que obtengas te dará acceso a más descubrimientos (encuentra un área oculta con el escarabajo volador o descubre formas ingeniosas de entorpecer a los enemigos con el látigo). Ya sea si estás buscando secretos, practicando el uso de la espada o mejorando tu equipamiento, en el juego The Legend of Zelda™: Skyward Sword HD podrás forjar tu propio destino.', 59990);
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (43, 'Zelda Breath of the Wild', 'Una épica aventura de mundo abierto en el universo de Zelda.', DATE '2017-03-03', 'Nintendo', 'Aventura/Acción', 'Nintendo Switch', 'E', 'Japonés, Inglés, Español', '13.4GB de espacio, conexión a internet', TIMESTAMP '2024-06-12 22:39:09.097000', TIMESTAMP '2024-06-12 22:39:09.097000', 1, 300, 'Olvida todo lo que sabes acerca de los juegos de la serie The Legend of Zelda. Explora y descubre un mundo lleno de aventuras en The Legend of Zelda: Breath of the Wild, una nueva saga que rompe los esquemas de la aclamada serie. Viaja a través de praderas y bosques, y alcanza cimas de montañas mientras descubres cómo cayó en la ruina el reino de Hyrule en esta emocionante aventura al aire libre. Ahora con Nintendo Switch, tu aventura será más libre y extensa que nunca. Lleva tu consola y vive una gran aventura como Link de la manera que más te guste.', 69990);
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (44, 'Zelda Tears of the Kingdom', 'La última entrega en la saga de aventuras de Zelda.', DATE '2023-05-12', 'Nintendo', 'Aventura/Acción', 'Nintendo Switch', 'E', 'Japonés, Inglés, Español', '15GB de espacio, conexión a internet', TIMESTAMP '2024-06-12 22:39:09.130000', TIMESTAMP '2024-06-12 22:39:09.130000', 1, 400, 'Una épica aventura a través de la superficie y los cielos de Hyrule te espera en The Legend of Zelda™: Tears of the Kingdom, disponible exclusivamente para la consola Nintendo Switch™.
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (41, 'Mario Wonder', 'Un emocionante nuevo juego de aventuras de Mario en el Mundo Maravilla.', DATE '2023-10-20', 'Nintendo', 'Aventura/Plataformas', 'Nintendo Switch', 'E', 'Japonés, Inglés, Español', '10GB de espacio, conexión a internet', TIMESTAMP '2024-06-12 22:23:54.007000', TIMESTAMP '2024-06-12 22:23:54.007000', 1, 300, 'Mario y sus amigos fueron invitados a visitar el colorido Reino Flor (que queda muy cerca del Reino Champiñón). Desgraciadamente, Bowser se ha transformado en un castillo volador y está causando caos en este pacífico territorio. ¡Ahora nuestros héroes deberán salvar el día (y al Reino Flor) en esta maravillosa nueva aventura!', 49990);
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (8, 'Sekiro: Shadows Die Twice', 'Juego de acción y aventura en un Japón feudal.', DATE '2019-03-22', 'FromSoftware', 'Acción/Aventura', 'PS5', 'M', 'Japonés, Inglés', '25GB de espacio, conexión a internet', TIMESTAMP '2024-06-11 19:55:57.280000', TIMESTAMP '2024-06-11 19:55:57.280000', 1, 150, 'Entra en una nueva experiencia de juego oscura y brutal de los creadores de Bloodborne y la serie Dark Souls.
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (39, 'Stardew Valley', 'Un juego de simulación agrícola donde puedes construir y gestionar tu propia granja.', DATE '2016-02-26', 'ConcernedApe', 'Simulación, RPG', 'PC, PS5, Xbox Series X/S, Nintendo Switch', 'Everyone 10+', 'Multilenguaje', 'Procesador: 2 Ghz | Memoria: 2 GB RAM | Gráficos: 256 mb video memory, shader model 3.0+', TIMESTAMP '2024-06-12 15:20:49.644000', TIMESTAMP '2024-06-12 15:20:49.644000', 1, 200, '¡Stardew Valley es un RPG de vida en el campo! Hereda la vieja granja de tu abuelo en Stardew Valley. Armado con herramientas de segunda mano y algo de dinero, te dispones a empezar tu nueva vida. ¿Podrás aprender a vivir de la tierra y a convertir esos campos de malezas en un hogar próspero? No va a ser fácil. Desde que Joja Corporation llegó a la ciudad, las antiguas formas de vivir han desaparecido. El centro comunitario, en su momento el centro de actividad más activo de la ciudad, se encuentra ahora en ruinas. Pero el valle se encuentra lleno de oportunidades. ¡Con un poco de dedicación, podrás ser uno de los que devuelva Stardew Valley a la gloria!', 14990);
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (40, 'Cuphead', 'Un juego de plataformas y acción con un estilo visual inspirado en los dibujos animados de los años 30.', DATE '2017-09-29', 'Studio MDHR', 'Acción, Plataforma', 'PC, Nintendo Switch', 'Everyone 10+', 'Inglés', 'Procesador: Intel Core2 Duo E8400 | Memoria: 3 GB RAM | Gráficos: GeForce 9600 GT', TIMESTAMP '2024-06-12 15:20:49.695000', TIMESTAMP '2024-06-12 15:20:49.695000', 1, 200, 'Cuphead es un juego clásico de acción de disparos muy enfocado en batallas de jefes. Inspirado en las caricaturas de 1930, las imágenes y el audio se crearon de forma minuciosa con las mismas técnicas de la era, esto es, animación tradicional a mano, fondos de marca de agua y grabaciones originales de jazz.
INSERT INTO C##JUEGOSMAX.JUEGOSMAX_JUEGO (ID, NOMBRE, DESCRIPCION, FECHA_LANZAMIENTO, DESARROLLADORA, GENERO, PLATAFORMA, CLASIFICACION, IDIOMA, REQUISITOS, FECHA_CREACION, FECHA_MODIFICACION, AVAILABLE, STOCK, DESCRIPCIONEXTENSA, PRECIO) VALUES (9, 'Resident Evil 2 Remake', 'Remake del clásico juego de terror y supervivencia.', DATE '2019-01-25', 'Capcom', 'Terror/Supervivencia', 'Xbox Series', 'M', 'Inglés, Español', '26GB de espacio, conexión a internet', TIMESTAMP '2024-06-11 19:55:57.286000', TIMESTAMP '2024-06-11 19:55:57.286000', 1, 200, 'Los protagonistas de la historia son, el novato oficial de policía Leon S. Kennedy, quien recientemente fue asignado al cuerpo de policía de Raccoon (R.P.D.) y la estudiante universitaria Claire Redfield, quien esta en busca de su hermano, el policía del grupo S.T.A.R.S. de la R.P.D. Chris Redfield, quien recientemente desapareció, los jóvenes tras encontrarse mientras huían de los zombis, se unen para escapar de la ciudad, mientras intentan escapar se separan, y en su recorrido se encuentran con otros supervivientes por la ciudad, la comisaría de policía, y descubren que Umbrella trata de apoderarse de una muestra del nuevo virus G para continuar con sus experimentos biológicos en seres humanos.', 29990);
